	this.plotDIVHeight = $(this.fuzzyTimelineContainer).height()-$(this.sliderTable).height();
	var plotScrollContainer = $("<div></div>");
	plotScrollContainer.css("overflow-x","auto");
	plotScrollContainer.css("overflow-y","hidden");
